"use strict";

var mailPath = './mail.php';
document.querySelectorAll('.uniForm').forEach(function (e) {
  e.addEventListener('submit', function (e) {
    var th = this,
      params = new FormData(this),
      request = new XMLHttpRequest();
    request.open('POST', mailPath, true);
    request.send(params);
    request.onreadystatechange = function () {
      if (this.readyState == 4 && this.status == 200) {
        setTimeout(function () {
          th.reset();
        }, 1000);
        alert('Спасибо! Мы ответим в ближайшее время.');
      }
    };
    e.preventDefault();
  });
});
